import React, { useContext } from 'react';
import { UserContext } from '../context/UserContext'; // Update the path
import { useProducts } from '../context/ProductsContext';


const ManageProductsPage = () => {
  const { products, loading } = useProducts();
  const { currentUser} = useContext(UserContext);

   // Ensure useEffect runs when currentUser changes

  const handleUpdate = (productId) => {
    // Implement update logic, e.g., redirect to update page
    console.log(`Update product with ID: ${productId}`);
  };

  const handleDelete = (productId) => {
    // Implement delete logic, e.g., show confirmation modal
    console.log(`Delete product with ID: ${productId}`);
  };

  if (currentUser && currentUser.role !== "Admin") {
    // Unauthorized access alert
    return (
      <alert variant="danger" className="text-center mt-4">
        You are not authorized to access this page.
      </alert>
    );
  }

  const isAdmin = currentUser && currentUser.role === 'Admin';


  return (
    <div>
      <h1 className='text-center'>Manage Products</h1>
      {loading ? (
        <p>Loading products...</p>
      ) : (
        <table className="table striped bordered hover">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products && products.map((product) => (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>
                  <button className='btn btn-info' onClick={() => handleUpdate(product.id)}>
                    Update
                  </button>{' '}
                  <button className='btn btn-danger' onClick={() => handleDelete(product.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ManageProductsPage;
