import React, { useContext, useEffect, useState } from 'react';
import { useProducts } from '../context/ProductsContext';

function Cart() {
    const { cartProducts, loading } = useProducts();
    const [total, setTotal] = useState(0);

    useEffect(() => {
        // Calculate total whenever cartProducts change
        const newTotal = cartProducts.reduce((acc, item) => acc + item.price * item.quantity, 0);
        setTotal(newTotal);
    }, [cartProducts]);

    const handleAdjustQuantity = (itemId, adjustment) => {
        const updatedCartProducts = cartProducts.map(item => {
            if (item.id === itemId) {
                // Ensure quantity doesn't go below 1
                const newQuantity = Math.max(item.quantity + adjustment, 1);
                return { ...item, quantity: newQuantity };
            }
            return item;
        });

        // Update the cartProducts state with the adjusted quantity
        // You may also want to send a request to update the server-side cart here
        // For simplicity, we're updating the local state only
        setTotal(calculateTotal(updatedCartProducts));
    };

    const calculateTotal = (items) => {
        return items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    };

    return (
        <div className="container mt-5">
            <h2>Your Cart</h2>
            {loading ? (
                <p>Loading...</p>
            ) : cartProducts.length > 0 ? (
                <div>
                    {cartProducts.map(item => (
                        <div key={item.id} className="mb-3">
                            <h3>{item.name}</h3>
                            <p>{item.description}</p>
                            <p>Price: ${item.price}</p>
                            <p>
                                Quantity: {item.quantity}
                                <button
                                    className="btn btn-primary ml-2"
                                    onClick={() => handleAdjustQuantity(item.id, 1)}
                                >
                                    +
                                </button>
                                <button
                                    className="btn btn-primary ml-2"
                                    onClick={() => handleAdjustQuantity(item.id, -1)}
                                >
                                    -
                                </button>
                            </p>
                        </div>
                    ))}
                    <div className="mt-4">
                        <h4>Total: ${total}</h4>
                    </div>
                </div>
            ) : (
                <div className="text-center">
                    <p className='display-1'>Your cart is empty</p>
                </div>
            )}
        </div>
    );
}

export default Cart;
