import React, { createContext, useState, useContext, useEffect } from "react";
import Swal from "sweetalert2";
import { UserContext } from "../context/UserContext";

const ProductsContext = createContext();

const ProductsProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [cartProducts, setCartProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  const { authToken } = useContext(UserContext);
  const url = "http://localhost:5000";



  useEffect(() => {
    const getProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(`${url}/products`);
        const data = await response.json();
        setProducts(data.products);
        setLoading(false);
      } catch (error) {
        console.error("Error Fetching Products: ", error);
      }
    };
    getProducts();
  }, []);

  console.log("Products: ", products);

  // Add items to cart
  const addToCart = async (productId) => {
    try {
        console.log('Adding to cart: ', productId);
        const response = await fetch(`${url}/add_to_cart/${productId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Accept: 'application/json',
                Authorization: `Bearer ${authToken}`,
            },
        });

        if (!response.ok) {
            const data = await response.json();
            console.error('Error Adding to Cart: ', data.message);
            Swal.fire({
                position: "center",
                icon: "error",
                title: data.message,
                showConfirmButton: false,
                timer: 1500
            })
            // Handle error state if needed
        } else {
            console.log('Product added to the cart successfully');
            Swal.fire({
                position: "center",
                icon: "success",
                title: "Product added to the cart successfully",
                showConfirmButton: false,
                timer: 1500
            })
            // Add item to the cartProducts state
            setCartProducts([...cartProducts, productId]);
            // You may want to update the cartProducts state here
        }
    } catch (error) {
        console.error('Error Adding to Cart: ', error);
        // Handle error state if needed
    }
};

  // Fetch items from the cart
  useEffect(() => {
    const getCartProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(`${url}/cart/items`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `Bearer ${authToken}`,
          },
        });
        const data = await response.json();
        setCartProducts(data.cart_items);
        setLoading(false);
      } catch (error) {
        console.error("Error Fetching Products: ", error);
      }
    };
    getCartProducts();
  }, [authToken]);

  console.log("Cart Products: ", cartProducts);
  return (
    <ProductsContext.Provider value={{ products, loading, cartProducts, addToCart }}>
      {children}
    </ProductsContext.Provider>
  );
};

const useProducts = () => {
  const context = useContext(ProductsContext);
  if (context === undefined) {
    throw new Error("useProducts must be used within a ProductsProvider");
  }
  return context;
};

export { ProductsProvider, useProducts };
