import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext'; // Update the path
import { useProducts } from '../context/ProductsContext';


const ManageProductsPage = () => {
  const { products, loading, addToCart } = useProducts();
  const { currentUser, url } = useContext(UserContext);

  useEffect(() => {
    if (currentUser && currentUser.role !== "Admin") {
      alert("You are not authorized to access this page.");
      return;
    }

    const getAllProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(`${url}/products`);
        const data = await response.json();
        setAllProducts(data.products);
        setLoading(false);
      } catch (error) {
        console.error("Error Fetching Products: ", error);
      }
    };

    getAllProducts();
  }, [currentUser, url]); // Ensure useEffect runs when currentUser changes

  console.log("allProducts", allProducts);
  const handleUpdate = (productId) => {
    // Implement update logic, e.g., redirect to update page
    console.log(`Update product with ID: ${productId}`);
  };

  const handleDelete = (productId) => {
    // Implement delete logic, e.g., show confirmation modal
    console.log(`Delete product with ID: ${productId}`);
  };

  if (currentUser && currentUser.role !== "Admin") {
    // Unauthorized access alert
    return (
      <alert variant="danger" className="text-center mt-4">
        You are not authorized to access this page.
      </alert>
    );
  }

  return (
    <div>
      <h1>Manage Products</h1>
      {loading ? (
        <p>Loading products...</p>
      ) : (
        <table className="striped bordered hover">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {allProducts && allProducts.map((product) => (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>
                  <button variant="info" onClick={() => handleUpdate(product.id)}>
                    Update
                  </button>{' '}
                  <button variant="danger" onClick={() => handleDelete(product.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ManageProductsPage;
