import React, { useContext, useEffect, useState } from 'react';
import { useProducts } from '../context/ProductsContext';

function Cart() {
    const { cartProducts, loading } = useProducts();
    const [total, setTotal] = useState(0);

    useEffect(() => {
        // Calculate total whenever cartProducts change
        const newTotal = cartProducts.reduce((acc, item) => acc + item.price * item.quantity, 0);
        setTotal(newTotal);
    }, [cartProducts]);

    const handleIncreaseQuantity = (itemId) => {
        // Check if the item is already in the cart
        const isItemInCart = cartProducts.find(item => item.id === itemId);

        if (isItemInCart) {
            const updatedCartProducts = cartProducts.map(item => {
                if (item.id === itemId) {
                    return { ...item, quantity: item.quantity + 1 };
                }
                return item;
            });

            setTotal(total + isItemInCart.price);
        } else {
            // If the item is not in the cart, add it with an initial quantity of 1
            const newItem = { id: itemId, quantity: 1 /* Initial quantity */, /* Add other item properties */ };
            const updatedCartProducts = [...cartProducts, newItem];

            setTotal(total + newItem.price);
        }

        // Update the cartProducts state with the increased quantity
        // You may also want to send a request to update the server-side cart here
        // For simplicity, we're updating the local state only
    };

    return (
        <div className="container mt-5">
            <h2>Your Cart</h2>
            {loading ? (
                <p>Loading...</p>
            ) : cartProducts.length > 0 ? (
                <div>
                    {cartProducts.map(item => (
                        <div key={item.id} className="mb-3">
                            <h3>{item.name}</h3>
                            <p>{item.description}</p>
                            <p>Price: ${item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            <button
                                className="btn btn-primary"
                                onClick={() => handleIncreaseQuantity(item.id)}
                            >
                                Increase Quantity
                            </button>
                        </div>
                    ))}
                    <div className="mt-4">
                        <h4>Total: ${total}</h4>
                    </div>
                </div>
            ) : (
                <div className="text-center">
                    <p>Your cart is empty</p>
                </div>
            )}
        </div>
    );
}

export default Cart;
