import React, { useContext } from "react";
import { UserContext } from "../context/UserContext"; // Update the path
import { useProducts } from "../context/ProductsContext";
import { useNavigate } from "react-router-dom";

const ManageProductsPage = () => {
    const { products, loading, DeleteProducts } = useProducts();
    const { currentUser } = useContext(UserContext);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    // Ensure useEffect runs when currentUser changes


    const handleUpdate = (product) => {
      setSelectedProduct(product);
      openModal();
    };

    const handleDelete = (product) => {
      DeleteProducts(product);
    };

    const handleModalSubmit = (updatedProduct) => {
      // Call your updateProduct function here with updatedProduct
      // You may also close the modal and handle success/error messages
      closeAndResetModal();
    };

    const openModal = () => {
      setIsModalOpen(true);
    };

    const closeModal = () => {
      setIsModalOpen(false);
    };

    const closeAndResetModal = () => {
      setSelectedProduct(null);
      closeModal();
    };

  const navigate = useNavigate();

  const isAdmin = currentUser && currentUser.role === "Admin";

  return (
    <div className="container">
      {isAdmin ? (
        <>
          <h1 className="text-center">Manage Products</h1>
          <button className="btn btn-primary mb-3" onClick={()=>navigate("/add-products")}>Add Product</button>
          {loading ? (
            <p>Loading products...</p>
          ) : (
            <table className="table table-striped table-bordered table-hover">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Description</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products &&
                  products.map((product) => (
                    <tr key={product.id}>
                      <td>{product.id}</td>
                      <td>{product.name}</td>
                      <td>{product.description}</td>
                      <td>{product.price}</td>
                      <td>{product.quantity}</td>
                      <td>
                        <button
                          className="btn btn-info"
                          onClick={() => handleUpdate(product)}
                        >
                          Update
                        </button>{" "}
                        <button
                          className="btn btn-danger"
                          onClick={() => handleDelete(product)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
        </>
      ) : (
        <div className="text-center">
          <div className="alert alert-danger mt-4" role="alert">
            You are not authorized to view this page.
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageProductsPage;
