import React, { useContext, useEffect, useState } from 'react';
import { useProducts } from '../context/ProductsContext';

function Cart() {
    const { cartProducts, loading, addToCart } = useProducts();
    const [total, setTotal] = useState(0);

    useEffect(() => {
        // Calculate total whenever cartProducts change
        const newTotal = cartProducts.reduce((acc, item) => acc + item.price * item.quantity, 0);
        setTotal(newTotal);
    }, [cartProducts]);

    const handleIncreaseQuantity = (itemId) => {
        const updatedCartProducts = cartProducts.map(item => {
            if (item.id === itemId) {
                return { ...item, quantity: item.quantity + 1 };
            }
            return item;
        });

        setTotal(total + cartProducts.find(item => item.id === itemId).price);
    };

    const handleDecreaseQuantity = (itemId) => {
        const updatedCartProducts = cartProducts.map(item => {
            if (item.id === itemId && item.quantity > 1) {
                return { ...item, quantity: item.quantity - 1 };
            }
            return item;
        });

        setTotal(total - cartProducts.find(item => item.id === itemId).price);
    };

    const handleDeleteItem = (itemId) => {
        const deletedItem = cartProducts.find(item => item.id === itemId);
        const updatedCartProducts = cartProducts.filter(item => item.id !== itemId);

        setTotal(total - deletedItem.price);
    };

    return (
        <div className="container mt-5">
            <h2>Your Cart</h2>
            {loading ? (
                <p>Loading...</p>
            ) : cartProducts.length > 0 ? (
                <div>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Description</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cartProducts.map(item => (
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.description}</td>
                                    <td>${item.price}</td>
                                    <td>
                                        <button
                                            className="btn btn-outline-primary"
                                            onClick={() => handleDecreaseQuantity(item.id)}
                                        >
                                            -
                                        </button>{' '}
                                        {item.quantity}{' '}
                                        <button
                                            className="btn btn-outline-primary"
                                            onClick={() => handleIncreaseQuantity(item.id)}
                                        >
                                            +
                                        </button>
                                    </td>
                                    <td>
                                        <button
                                            className="btn btn-danger"
                                            onClick={() => {
                                                handleDeleteItem(item.id);
                                            }}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <div className="mt-4">
                        <h4>Total: ${total}</h4>
                    </div>
                </div>
            ) : (
                <div className="text-center">
                    <p className="display-1">Your cart is empty</p>
                </div>
            )}
        </div>
    );
}

export default Cart;
